"""
[  0   ] [  1   ] [  2   ]
11111111 -------- -------- header
-------- 1111---- -------- remVersion
-------- ----1111 -------- id
-------- -------- 11111111 sequenceNumber
"""

import numpy as np
from . import REM_BaseTypes



class RobotAssuredAck:
    header = 0                # integer [0, 255]             Header byte indicating the type of packet
    remVersion = 0            # integer [0, 15]              Version of roboteam_embedded_messages
    id = 0                    # integer [0, 15]              Id of the robot
    sequenceNumber = 0        # integer [0, 255]             Number to match this packet with AssuredPacket



# ================================ GETTERS ================================
    @staticmethod
    def get_header(payload):
        return ((payload[0]));

    @staticmethod
    def get_remVersion(payload):
        return ((payload[1] & 0b11110000) >> 4);

    @staticmethod
    def get_id(payload):
        return ((payload[1] & 0b00001111));

    @staticmethod
    def get_sequenceNumber(payload):
        return ((payload[2]));

# ================================ SETTERS ================================
    @staticmethod
    def set_header(payload, header):
        payload[0] = header;

    @staticmethod
    def set_remVersion(payload, remVersion):
        payload[1] = ((remVersion << 4) & 0b11110000) | (payload[1] & 0b00001111);

    @staticmethod
    def set_id(payload, id):
        payload[1] = (id & 0b00001111) | (payload[1] & 0b11110000);

    @staticmethod
    def set_sequenceNumber(payload, sequenceNumber):
        payload[2] = sequenceNumber;

# ================================ ENCODE ================================
    def encode(self):
        payload = np.zeros(REM_BaseTypes.PACKET_SIZE_ROBOT_ASSURED_ACK, dtype=np.uint8)
        RobotAssuredAck.set_header              (payload, self.header)
        RobotAssuredAck.set_remVersion          (payload, self.remVersion)
        RobotAssuredAck.set_id                  (payload, self.id)
        RobotAssuredAck.set_sequenceNumber      (payload, self.sequenceNumber)
        return payload


# ================================ DECODE ================================
    def decode(self, payload):
        self.header           = RobotAssuredAck.get_header(payload)
        self.remVersion       = RobotAssuredAck.get_remVersion(payload)
        self.id               = RobotAssuredAck.get_id(payload)
        self.sequenceNumber   = RobotAssuredAck.get_sequenceNumber(payload)


    def print_bit_string(self):
        payload = self.encode()
        for i in range(len(payload)):
            print(format(payload[i], '08b'), end=" ")
        print()
