import numpy as np
from . import REM_BaseTypes



class REM_BasestationLog:
    header = 0                # integer [0, 255]             Header byte indicating the type of packet
    remVersion = 0            # integer [0, 15]              Version of roboteam_embedded_messages
    messageLength = 0         # integer [0, 255]             Length of the following message



# ================================ GETTERS ================================
    @staticmethod
    def get_header(payload):
        return ((payload[0]));

    @staticmethod
    def get_remVersion(payload):
        return ((payload[1] & 0b11110000) >> 4);

    @staticmethod
    def get_messageLength(payload):
        return ((payload[1] & 0b00001111) << 4) | ((payload[2] & 0b11110000) >> 4);

# ================================ SETTERS ================================
    @staticmethod
    def set_header(payload, header):
        payload[0] = header;

    @staticmethod
    def set_remVersion(payload, remVersion):
        payload[1] = ((remVersion << 4) & 0b11110000) | (payload[1] & 0b00001111);

    @staticmethod
    def set_messageLength(payload, messageLength):
        payload[1] = ((messageLength >> 4) & 0b00001111) | (payload[1] & 0b11110000);
        payload[2] = ((messageLength << 4) & 0b11110000) | (payload[2] & 0b00001111);

# ================================ ENCODE ================================
    def encode(self):
        payload = np.zeros(REM_BaseTypes.PACKET_SIZE_REM_BASESTATION_LOG, dtype=np.uint8)
        REM_BasestationLog.set_header              (payload, self.header)
        REM_BasestationLog.set_remVersion          (payload, self.remVersion)
        REM_BasestationLog.set_messageLength       (payload, self.messageLength)
        return payload


# ================================ DECODE ================================
    def decode(self, payload):
        self.header           = REM_BasestationLog.get_header(payload)
        self.remVersion       = REM_BasestationLog.get_remVersion(payload)
        self.messageLength    = REM_BasestationLog.get_messageLength(payload)


    def print_bit_string(self):
        payload = self.encode()
        for i in range(len(payload)):
            print(format(payload[i], '08b'), end=" ")
        print()
